g = ''
k = 12
L = 576
t = 20

#Ans:['ACACGTTGTCAG', 'TAGACTCTCCGC', 'CATGCCCTGGCT']


def clumpfinding(g, k, L, t):
    '''For a genome g, finds sequences of length k that appear in a window L 
    at least t times.
    
    Algorithm works by sliding a window of length L along the genome. Then a 
    subwindow is slid across L and all possible kmers are added to a 
    dictionary with the values as the number of times they appear in L.
    '''
    gd = {}
    for n in np.arange(len(g)):
        wind = g[n:n+L]
        if len(wind) < L:
            break

        wd = {}
        for m in np.arange(L):
            kmer = wind[m:m+k]
            if len(kmer) < 5:
                continue
            if kmer not in wd:
                wd[kmer] = 0
            wd[kmer] = wd[kmer]+1

            for key, val in wd.iteritems():
                if val == t:
                    gd[key] = 1
                    
        
    print(gd.keys())

#clumpfinding(g, k, L, t)

cProfile.run('clumpfinding(g, k, L, t)', 'prof_clumpfinding1')
p = pstats.Stats('prof_clumpfinding1')
p.strip_dirs().sort_stats(-1).print_stats()
p.sort_stats('cumulative').print_stats(10)
